#include <stdbool.h>

bool isGood(int* nums, int numsSize) {
    int maxElement = 0;
    int count[201] = {0}; // since 1 <= num[i] <= 200

    // Find the maximum element and count occurrences of each element
    for (int i = 0; i < numsSize; i++) {
        maxElement = (nums[i] > maxElement) ? nums[i] : maxElement;
        count[nums[i]]++;
    }

    // Check if the array size is appropriate
    if (numsSize != maxElement + 1) {
        return false;
    }

    // Check if each element from 1 to maxElement - 1 appears once, and maxElement appears twice
    for (int i = 1; i <= maxElement; i++) {
        if (i < maxElement && count[i] != 1) {
            return false;
        }
        if (i == maxElement && count[i] != 2) {
            return false;
        }
    }

    return true;
}
