class Solution {
    public boolean isGood(int[] nums) {
        int n = 0;
        // Find the maximum element in nums
        for (int num : nums) {
            n = Math.max(n, num);
        }

        // Check if the length of nums is n + 1
        if (nums.length != n + 1) {
            return false;
        }

        // Count the occurrences of each number in nums
        int[] count = new int[n + 1];
        for (int num : nums) {
            count[num]++;
        }

        // Check if the count of n is exactly 2 and others are exactly 1
        for (int i = 1; i <= n; i++) {
            if (i == n && count[i] != 2) {
                return false;
            } else if (i != n && count[i] != 1) {
                return false;
            }
        }

        return true;
    }
}
