/**
 * @param {number[]} nums
 * @return {boolean}
 */
var isGood = function(nums) {
    // Find the maximum value in the array
    let maxVal = Math.max(...nums);

    // Check if the length of nums is equal to n + 1
    if (nums.length !== maxVal + 1) {
        return false;
    }

    // Create a frequency map
    let frequencyMap = {};
    for (let num of nums) {
        frequencyMap[num] = (frequencyMap[num] || 0) + 1;
    }

    // Check the frequency of each number
    for (let i = 1; i <= maxVal; i++) {
        if (i === maxVal && frequencyMap[i] !== 2) {
            // The number n should appear twice
            return false;
        } else if (i !== maxVal && frequencyMap[i] !== 1) {
            // Numbers from 1 to n-1 should appear once
            return false;
        }
    }

    // If all conditions are met
    return true;
};
