class Solution {
    func isGood(_ nums: [Int]) -> Bool {
        let n = nums.max() ?? 0 // Step 1: Find the maximum number which will be our candidate for n
        if nums.count != n + 1 { return false } // Step 2: Check if the length of nums is n + 1

        var frequency = [Int: Int]() // Step 3: Create a frequency map
        for num in nums {
            frequency[num, default: 0] += 1
        }

        for i in 1..<n { // Step 4: Check each number from 1 to n - 1
            if frequency[i] != 1 {
                return false
            }
        }

        // Finally, check if n appears exactly twice
        return frequency[n] == 2
    }
}
