function isGood(nums: number[]): boolean {
    const maxNum = Math.max(...nums);

    // Check if the length of nums is n + 1
    if (nums.length !== maxNum + 1) {
        return false;
    }

    const countMap: { [key: number]: number } = {};

    for (let num of nums) {
        countMap[num] = (countMap[num] || 0) + 1;
    }

    // Check the occurrence of each number
    for (let i = 1; i <= maxNum; i++) {
        if (i === maxNum) {
            // 'n' should appear twice
            if (countMap[i] !== 2) {
                return false;
            }
        } else {
            // Other numbers should appear once
            if (countMap[i] !== 1) {
                return false;
            }
        }
    }

    return true;
}
