/**
 * @param {string} s1
 * @param {string} s2
 * @return {boolean}
 */
var canBeEqual = function(s1, s2) {
    // Create arrays to compare characters at even and odd indices separately
    let evenCharsS1 = [s1[0], s1[2]];
    let oddCharsS1 = [s1[1], s1[3]];
    let evenCharsS2 = [s2[0], s2[2]];
    let oddCharsS2 = [s2[1], s2[3]];

    // Sort the arrays to compare regardless of order
    evenCharsS1.sort();
    oddCharsS1.sort();
    evenCharsS2.sort();
    oddCharsS2.sort();

    // Compare sorted arrays of even and odd indexed characters
    return evenCharsS1.join('') === evenCharsS2.join('') && oddCharsS1.join('') === oddCharsS2.join('');
};

// Example usage
console.log(canBeEqual("bnxw", "bwxn")); // Output: true
console.log(canBeEqual("abcd", "cdab")); // Output: true
console.log(canBeEqual("abcd", "dacb")); // Output: false
