public class Solution {
    public int IncremovableSubarrayCount(int[] nums) {
        int n = nums.Length;
        int count = 0;

        for (int start = 0; start < n; start++) {
            for (int end = start; end < n; end++) {
                if (IsIncremovable(nums, start, end)) {
                    count++;
                }
            }
        }

        return count;
    }

    private bool IsIncremovable(int[] nums, int start, int end) {
        int prev = start > 0 ? nums[start - 1] : int.MinValue;
        int next = end < nums.Length - 1 ? nums[end + 1] : int.MaxValue;

        // Check if the array segments before and after the subarray are strictly increasing
        if (prev >= next) return false;

        for (int i = 1; i < start; i++) {
            if (nums[i - 1] >= nums[i]) return false;
        }

        for (int i = end + 2; i < nums.Length; i++) {
            if (nums[i - 1] >= nums[i]) return false;
        }

        return true;
    }
}
