#include <vector>

class Solution {
public:
    int incremovableSubarrayCount(std::vector<int>& nums) {
        int count = 0;
        int n = nums.size();

        for (int i = 0; i < n; ++i) {
            for (int j = i; j < n; ++j) {
                // Check if subarray from i to j is incremovable
                if ((i == 0 || nums[i - 1] < nums[j + 1]) && (j == n - 1 || nums[i] < nums[j + 2])) {
                    count++;
                }
            }
        }

        return count;
    }
};
