int incremovableSubarrayCount(int* nums, int numsSize) {
    int count = 0;

    // Iterate through all subarrays
    for (int start = 0; start < numsSize; ++start) {
        for (int end = start; end < numsSize; ++end) {
            // Check if removing the current subarray makes the remaining array strictly increasing
            int prev = (start > 0) ? nums[start - 1] : -1; // Element before the subarray (if exists)
            int next = (end < numsSize - 1) ? nums[end + 1] : 51; // Element after the subarray (if exists)

            if (prev < next) {
                bool isIncremovable = true;
                for (int i = start; i < end && isIncremovable; ++i) {
                    if (nums[i] >= nums[i + 1]) {
                        isIncremovable = false;
                    }
                }
                if (isIncremovable) {
                    ++count;
                }
            }
        }
    }

    return count;
}
