int incremovableSubarrayCount(int* nums, int numsSize) {
    int count = 0;

    // Function to check if a segment of the array is strictly increasing
    bool isStrictlyIncreasing(int start, int end) {
        for (int i = start; i < end; i++) {
            if (nums[i] >= nums[i + 1]) {
                return false;
            }
        }
        return true;
    }

    // Iterate through all subarrays
    for (int start = 0; start <= numsSize; ++start) {
        for (int end = start; end <= numsSize; ++end) {
            // Check segments before and after the subarray
            if (isStrictlyIncreasing(0, start - 1) &&
                isStrictlyIncreasing(end, numsSize - 1) &&
                (start == 0 || end == numsSize || nums[start - 1] < nums[end])) {
                ++count;
            }
        }
    }

    return count;
}
