class Solution {
    public int incremovableSubarrayCount(int[] nums) {
        int n = nums.length;
        int count = 0;

        for (int i = 0; i < n; i++) {
            for (int j = i; j < n; j++) {
                // Check if the subarray from i to j (inclusive) is incremovable
                if (isIncremovable(nums, i, j)) {
                    count++;
                }
            }
        }

        return count;
    }

    private boolean isIncremovable(int[] nums, int start, int end) {
        int prev = (start == 0) ? Integer.MIN_VALUE : nums[start - 1];
        int next = (end == nums.length - 1) ? Integer.MAX_VALUE : nums[end + 1];

        // Check if elements before the subarray are strictly increasing
        for (int i = 0; i < start; i++) {
            if (nums[i] >= prev) {
                return false;
            }
            prev = nums[i];
        }

        // Check if elements after the subarray are strictly increasing
        for (int i = end + 1; i < nums.length; i++) {
            if (nums[i] <= next) {
                return false;
            }
            next = nums[i];
        }

        return true;
    }
}
