/**
 * @param {number[]} nums
 * @return {number}
 */
var incremovableSubarrayCount = function(nums) {
    let count = 0;

    for (let start = 0; start < nums.length; start++) {
        for (let end = start; end < nums.length; end++) {
            if (isIncremovable(nums, start, end)) {
                count++;
            }
        }
    }

    return count;
};

function isIncremovable(nums, start, end) {
    let leftNum = start > 0 ? nums[start - 1] : -Infinity;
    let rightNum = end < nums.length - 1 ? nums[end + 1] : Infinity;

    // Check if removing the subarray [start, end] makes the array strictly increasing
    if (leftNum >= rightNum) {
        return false;
    }

    for (let i = 0; i < start; i++) {
        if (nums[i] >= nums[i + 1]) {
            return false;
        }
    }

    for (let i = end + 1; i < nums.length - 1; i++) {
        if (nums[i] >= nums[i + 1]) {
            return false;
        }
    }

    return true;
}
