class Solution {
    fun incremovableSubarrayCount(nums: IntArray): Int {
        var count = 0
        val n = nums.size

        for (start in 0 until n) {
            for (end in start until n) {
                if (isIncremovable(nums, start, end)) {
                    count++
                }
            }
        }

        return count
    }

    private fun isIncremovable(nums: IntArray, start: Int, end: Int): Boolean {
        var prev = if (start > 0) nums[start - 1] else Int.MIN_VALUE
        var next = if (end < nums.size - 1) nums[end + 1] else Int.MAX_VALUE

        return prev < next
    }
}
