class Solution {
    fun incremovableSubarrayCount(nums: IntArray): Int {
        val n = nums.size
        var count = 0

        // Arrays to store the maximum to the left and minimum to the right
        val maxLeft = IntArray(n) { 0 }
        val minRight = IntArray(n) { 0 }

        // Compute maxLeft
        maxLeft[0] = Int.MIN_VALUE
        for (i in 1 until n) {
            maxLeft[i] = maxOf(maxLeft[i - 1], nums[i - 1])
        }

        // Compute minRight
        minRight[n - 1] = Int.MAX_VALUE
        for (i in n - 2 downTo 0) {
            minRight[i] = minOf(minRight[i + 1], nums[i + 1])
        }

        // Check each subarray
        for (i in 0 until n) {
            for (j in i until n) {
                if (maxLeft[i] < minRight[j]) {
                    count++
                }
            }
        }

        return count
    }
}
