class Solution {

    /**
     * @param Integer[] $nums
     * @return Integer
     */
    function incremovableSubarrayCount($nums) {
        $n = count($nums);
        $count = 0;

        // Function to check if the array is strictly increasing
        $isStrictlyIncreasing = function($arr) {
            for ($i = 1; $i < count($arr); $i++) {
                if ($arr[$i] <= $arr[$i - 1]) {
                    return false;
                }
            }
            return true;
        };

        // Iterate over all subarrays
        for ($i = 0; $i < $n; $i++) {
            for ($j = $i; $j < $n; $j++) {
                // Remove the subarray from $i to $j
                $newArray = array_merge(array_slice($nums, 0, $i), array_slice($nums, $j + 1));
                // Check if the remaining array is strictly increasing
                if ($isStrictlyIncreasing($newArray)) {
                    $count++;
                }
            }
        }

        return $count;
    }
}
