object Solution {
    def incremovableSubarrayCount(nums: Array[Int]): Int = {
        def isStrictlyIncreasing(arr: Array[Int]): Boolean = {
            for(i <- 1 until arr.length) {
                if(arr(i-1) >= arr(i)) return false
            }
            true
        }

        var count = 0
        for(i <- nums.indices) {
            for(j <- i until nums.length) {
                val subarrayRemoved = nums.slice(0, i) ++ nums.slice(j + 1, nums.length)
                if(isStrictlyIncreasing(subarrayRemoved)) {
                    count += 1
                }
            }
        }
        count
    }
}
