function incremovableSubarrayCount(nums: number[]): number {
    let count = 0;

    for (let start = 0; start < nums.length; start++) {
        for (let end = start; end < nums.length; end++) {
            if (isIncremovable(nums, start, end)) {
                count++;
            }
        }
    }

    return count;
}

function isIncremovable(nums: number[], start: number, end: number): boolean {
    let left = start > 0 ? nums[start - 1] : -Infinity;
    let right = end < nums.length - 1 ? nums[end + 1] : Infinity;

    // Check if the elements before and after the subarray are in strictly increasing order.
    for (let i = 0; i < start; i++) {
        if (nums[i] >= left) return false;
        left = nums[i];
    }

    for (let i = end + 1; i < nums.length; i++) {
        if (nums[i] <= right) return false;
        right = nums[i];
    }

    // Check if removing the subarray makes the sequence strictly increasing
    return left < right;
}
