function incremovableSubarrayCount(nums: number[]): number {
    let count = 0;

    // Iterate over all subarrays
    for (let start = 0; start < nums.length; start++) {
        for (let end = start; end < nums.length; end++) {
            // Check if removing the subarray [start, end] makes the array strictly increasing
            if (isStrictlyIncreasingAfterRemoval(nums, start, end)) {
                count++;
            }
        }
    }

    return count;
}

function isStrictlyIncreasingAfterRemoval(nums: number[], start: number, end: number): boolean {
    let prev = start > 0 ? nums[start - 1] : -Infinity;

    // Check elements after the end of the subarray
    for (let i = end + 1; i < nums.length; i++) {
        if (nums[i] <= prev) {
            return false;
        }
        prev = nums[i];
    }

    return true;
}
