-spec distribute_candies(N :: integer(), Limit :: integer()) -> integer().
distribute_candies(N, Limit) ->
    distribute_candies_helper(N, Limit, 0, 0, 0).

distribute_candies_helper(0, _, _, _, Count) ->
    Count + 1;
distribute_candies_helper(N, Limit, A, B, Count) when N > 0 ->
    NewCount = if 
        A =< Limit, B =< Limit, N - A - B =< Limit ->
            Count + 1;
        true ->
            Count
    end,
    Count1 = if 
        A < Limit -> distribute_candies_helper(N, Limit, A + 1, B, NewCount);
        true -> NewCount
    end,
    Count2 = if 
        B < Limit -> distribute_candies_helper(N, Limit, A, B + 1, Count1);
        true -> Count1
    end,
    Count2.
