/**
 * @param {number} n
 * @param {number} limit
 * @return {number}
 */
var distributeCandies = function(n, limit) {
    // Initialize a 3D array with zeros
    let dp = Array.from({ length: n + 1 }, () =>
        Array.from({ length: limit + 1 }, () => Array(limit + 1).fill(0))
    );

    // Base case: one way to distribute 0 candies
    for (let j = 0; j <= limit; j++) {
        for (let k = 0; k <= limit; k++) {
            dp[0][j][k] = 1;
        }
    }

    // Fill the dp array
    for (let i = 1; i <= n; i++) {
        for (let j = 0; j <= limit; j++) {
            for (let k = 0; k <= limit; k++) {
                let sum = 0;
                for (let x = 0; x <= j && x <= i; x++) {
                    sum += dp[i - x][k][limit - k];
                }
                dp[i][j][k] = sum;
            }
        }
    }

    // Sum up all the ways to distribute n candies
    let totalWays = 0;
    for (let j = 0; j <= limit; j++) {
        for (let k = 0; k <= limit; k++) {
            totalWays += dp[n][j][k];
        }
    }

    return totalWays;
};
