object Solution {
    def distributeCandies(n: Int, limit: Int): Int = {
        val memo = Array.ofDim[Int](n + 1, limit + 1, limit + 1)

        for {
            i <- 0 to n
            j <- 0 to limit
            k <- 0 to limit
        } memo(i)(j)(k) = -1

        def ways(candies: Int, max1: Int, max2: Int): Int = {
            if (candies == 0) return 1
            if (max1 < 0 || max2 < 0) return 0
            if (memo(candies)(max1)(max2) != -1) return memo(candies)(max1)(max2)

            var count = 0
            for {
                x <- 0 to Math.min(candies, max1)
                y <- 0 to Math.min(candies - x, max2)
            } {
                val z = candies - x - y
                if (z <= limit) count += ways(candies - x - y, x, y)
            }

            memo(candies)(max1)(max2) = count
            count
        }

        ways(n, limit, limit)
    }
}
