#include <vector>
#include <climits> // For INT_MAX

using namespace std;

class Solution {
public:
    int minimumCost(vector<int>& nums) {
        int n = nums.size();
        int minCost = INT_MAX;

        // Iterate through the array to find all possible positions for the first two splits
        for (int i = 1; i < n - 1; ++i) {
            for (int j = i + 1; j < n; ++j) {
                // nums[0] to nums[i-1] is the first subarray
                // nums[i] to nums[j-1] is the second subarray
                // nums[j] to nums[n-1] is the third subarray
                int cost = nums[0] + nums[i] + nums[j];
                minCost = min(minCost, cost);
            }
        }

        return minCost;
    }
};
