class Solution {
    fun minimumCost(nums: IntArray): Int {
        val n = nums.size
        // dp[i][j] represents the minimum cost to split the first i numbers into j subarrays
        val dp = Array(n + 1) { IntArray(4) { Int.MAX_VALUE / 2 } } // Use a large number to avoid overflow

        // Base case: 0 cost to split 0 numbers into 0 subarrays
        dp[0][0] = 0

        // Iterate over the array
        for (i in 1..n) {
            for (j in 1..3) {
                for (k in 1..i) {
                    // Update the dp array with the minimum cost
                    dp[i][j] = minOf(dp[i][j], dp[k - 1][j - 1] + nums[k - 1])
                }
            }
        }

        // The result is in dp[n][3] as we want to split the whole array (n elements) into 3 parts
        return dp[n][3]
    }
}
