object Solution {
    def minimumCost(nums: Array[Int]): Int = {
        val n = nums.length
        val dp = Array.ofDim[Int](n, 4)

        // Initialize the first column (0 subarrays) to 0 as there is no cost
        for (i <- 0 until n) dp(i)(0) = 0

        // Loop for each subarray count from 1 to 3
        for (j <- 1 to 3) {
            // Set initial value for the first j-1 elements to Integer.MAX_VALUE as it's not possible to form j subarrays
            for (i <- 0 until j-1) dp(i)(j) = Int.MaxValue

            // Start from the j-1 element as that's the first position where j subarrays can be formed
            for (i <- j-1 until n) {
                dp(i)(j) = Int.MaxValue
                for (k <- j-2 to i) {
                    if (dp(k)(j-1) != Int.MaxValue) {
                        dp(i)(j) = dp(i)(j) min (dp(k)(j-1) + nums(k+1))
                    }
                }
            }
        }

        // The answer is in dp(n-1)(3)
        dp(n-1)(3)
    }
}
