using System;
using System.Collections.Generic;

public class Solution {
    public int MaximumNumberOfStringPairs(string[] words) {
        var reverseCount = new Dictionary<string, int>();
        int pairCount = 0;

        foreach (var word in words) {
            string reversed = new string(word.Reverse().ToArray());

            if (reverseCount.ContainsKey(reversed)) {
                pairCount++;
                reverseCount[reversed]--;
                if (reverseCount[reversed] == 0) {
                    reverseCount.Remove(reversed);
                }
            } else {
                if (!reverseCount.ContainsKey(word)) {
                    reverseCount[word] = 0;
                }
                reverseCount[word]++;
            }
        }

        return pairCount;
    }
}
