#include <string>
#include <vector>
#include <unordered_map>
using namespace std;

class Solution {
public:
    int maximumNumberOfStringPairs(vector<string>& words) {
        unordered_map<string, int> reversedWords;
        int pairs = 0;

        // Count the occurrences of each reversed word
        for (const string& word : words) {
            string reversedWord = word;
            reverse(reversedWord.begin(), reversedWord.end());
            reversedWords[reversedWord]++;
        }

        // Find pairs
        for (const string& word : words) {
            if (reversedWords[word] > 0) {
                pairs++;
                reversedWords[word]--;
            }
        }

        return pairs / 2; // each pair is counted twice
    }
};
