class Solution {
  int maximumNumberOfStringPairs(List<String> words) {
    Map<String, int> wordCount = {};
    int pairs = 0;

    // Count the occurrences of each word
    for (var word in words) {
      wordCount[word] = (wordCount[word] ?? 0) + 1;
    }

    // Check each word for a possible pair
    for (var word in words) {
      var reversed = word.split('').reversed.join();

      if (wordCount[reversed] != null && wordCount[reversed]! > 0) {
        // If the word is its own reverse, ensure there are at least two occurrences
        if (word == reversed && wordCount[word]! < 2) {
          continue;
        }
        pairs++;
        wordCount[reversed] = wordCount[reversed]! - 1; // Decrement the count
      }
    }

    // Divide by 2 because each pair is counted twice
    return pairs ~/ 2;
  }
}
