/**
 * @param {string[]} words
 * @return {number}
 */
var maximumNumberOfStringPairs = function(words) {
    let reversedWordsSet = new Set();
    let pairs = 0;

    for (let word of words) {
        let reversed = word.split('').reverse().join('');
        if (reversedWordsSet.has(reversed)) {
            pairs++;  // Increment the pair count
            reversedWordsSet.delete(reversed);  // Remove the reversed word to avoid double counting
        } else {
            reversedWordsSet.add(word);  // Add the word for future matching
        }
    }

    return pairs;
};

// Example usage
console.log(maximumNumberOfStringPairs(["cd","ac","dc","ca","zz"])); // Output: 2
console.log(maximumNumberOfStringPairs(["ab","ba","cc"])); // Output: 1
console.log(maximumNumberOfStringPairs(["aa","ab"])); // Output: 0
