class Solution {

    /**
     * @param String[] $words
     * @return Integer
     */
    function maximumNumberOfStringPairs($words) {
        $reverseCount = [];
        $pairCount = 0;

        foreach ($words as $word) {
            $reversedWord = strrev($word);

            // Check if the reversed word exists in the array
            if (isset($reverseCount[$reversedWord]) && $reverseCount[$reversedWord] > 0) {
                // A pair is found
                $pairCount++;
                // Decrement the count of the reversed word as it has been paired
                $reverseCount[$reversedWord]--;
            } else {
                // Add or increment the count of the word
                if (!isset($reverseCount[$word])) {
                    $reverseCount[$word] = 1;
                } else {
                    $reverseCount[$word]++;
                }
            }
        }

        return $pairCount;
    }
}
