class Solution {
    fun findMissingAndRepeatedValues(grid: Array<IntArray>): IntArray {
        val n = grid.size
        val count = IntArray(n * n + 1) { 0 }

        // Count the frequency of each number in the grid
        for (row in grid) {
            for (value in row) {
                count[value]++
            }
        }

        var repeated = -1
        var missing = -1

        // Find the repeated and missing numbers
        for (i in 1..n * n) {
            if (count[i] == 2) {
                repeated = i
            } else if (count[i] == 0) {
                missing = i
            }
        }

        return intArrayOf(repeated, missing)
    }
}
