use std::collections::HashMap;

impl Solution {
    pub fn find_missing_and_repeated_values(grid: Vec<Vec<i32>>) -> Vec<i32> {
        let n = grid.len();
        let mut freq_map = HashMap::new();

        // Count the frequency of each number in the grid
        for i in 0..n {
            for j in 0..n {
                *freq_map.entry(grid[i][j]).or_insert(0) += 1;
            }
        }

        let mut missing = 0;
        let mut repeated = 0;

        // Find the missing and repeated numbers
        for num in 1..=(n * n) as i32 {
            match freq_map.get(&num) {
                Some(&count) => {
                    if count == 2 {
                        repeated = num;
                    }
                },
                None => {
                    missing = num;
                },
            }
        }

        vec![repeated, missing]
    }
}
