public class Solution {
    public int[] CircularGameLosers(int n, int k) {
        bool[] hasBall = new bool[n];
        int[] losers = new int[n];
        int loserCount = 0;
        int current = 0; // Start from the 1st friend
        int steps = 0;

        // Mark the first friend as having received the ball
        hasBall[current] = true;

        while (true) {
            steps++;
            current = (current + steps * k) % n; // Find the next friend to pass the ball

            // If the current friend already received the ball, break the loop
            if (hasBall[current]) break;

            // Mark the current friend as having received the ball
            hasBall[current] = true;
        }

        // Find all friends who did not receive the ball
        for (int i = 0; i < n; i++) {
            if (!hasBall[i]) losers[loserCount++] = i + 1; // Convert 0-based index to 1-based
        }

        // Resize the losers array to contain only the losers
        int[] finalLosers = new int[loserCount];
        System.Array.Copy(losers, finalLosers, loserCount);

        return finalLosers;
    }
}
