public class Solution {
    public int[] CircularGameLosers(int n, int k) {
        bool[] hasReceivedBall = new bool[n];
        int currentPlayer = 0; // Start at the first friend
        int turn = 0;

        while (true) {
            currentPlayer = (currentPlayer + k - 1) % n; // Find the next player to receive the ball
            turn++;

            if (hasReceivedBall[currentPlayer]) {
                // If this player has already received the ball, end the game
                break;
            }

            hasReceivedBall[currentPlayer] = true;
            k = turn * k; // Increase the step count for the next turn
        }

        // Collect all friends who never received the ball
        List<int> losers = new List<int>();
        for (int i = 0; i < n; i++) {
            if (!hasReceivedBall[i]) {
                losers.Add(i + 1); // Convert 0-indexed to 1-indexed
            }
        }

        return losers.ToArray();
    }
}
