#include <vector>
using namespace std;

class Solution {
public:
    vector<int> circularGameLosers(int n, int k) {
        vector<bool> hasBall(n, false);  // Track if a friend has received the ball
        vector<int> losers;
        int current = 0;  // Starting at the 1st friend, index 0

        for (int i = 1; i <= n; ++i) {
            // Calculate next position, considering the current friend in the counting
            current = (current + k - 1) % n;
            if (hasBall[current]) break;  // If a friend gets the ball a second time, game ends
            hasBall[current] = true;
        }

        // Identifying the losers (those who never received the ball)
        for (int i = 0; i < n; ++i) {
            if (!hasBall[i]) {
                losers.push_back(i + 1);  // Friend numbering starts from 1
            }
        }

        return losers;
    }
};
