#include <vector>
using namespace std;

class Solution {
public:
    vector<int> circularGameLosers(int n, int k) {
        vector<bool> hasReceivedBall(n, false);  // Track if a friend has received the ball
        vector<int> losers;
        int current = 0;  // Starting at the 1st friend
        int passes = 0;   // Count the number of passes

        while (true) {
            current = (current + k - 1) % n;  // Calculate next position
            if (hasReceivedBall[current]) break;  // End game if a friend receives the ball a second time
            hasReceivedBall[current] = true;
            passes++;
        }

        // Identifying the losers (those who never received the ball)
        for (int i = 0; i < n; ++i) {
            if (!hasReceivedBall[i]) {
                losers.push_back(i + 1);  // Friend numbering starts from 1
            }
        }

        return losers;
    }
};
