#include <stdlib.h>

/**
 * Note: The returned array must be malloced, assume caller calls free().
 */
int* circularGameLosers(int n, int k, int* returnSize){
    int* received = (int*)calloc(n, sizeof(int)); // Array to track if a friend has received the ball
    int current = 0; // Start with the 1st friend
    int passCount = 0; // To count the number of passes made

    while (1) {
        passCount++;
        current = (current + passCount * k - 1) % n; // Calculate the next friend to receive the ball
        received[current]++;

        if (received[current] > 1) {
            // If a friend receives the ball for the second time, the game ends
            break;
        }
    }

    // Count the number of losers
    int losersCount = 0;
    for (int i = 0; i < n; i++) {
        if (received[i] == 0) {
            losersCount++;
        }
    }

    *returnSize = losersCount;
    int* losers = (int*)malloc(losersCount * sizeof(int));
    int idx = 0;

    // Fill the array with the losers
    for (int i = 0; i < n; i++) {
        if (received[i] == 0) {
            losers[idx++] = i + 1; // Friends are 1-indexed
        }
    }

    free(received); // Free the temporary array
    return losers;
}
