#include <stdlib.h>

/**
 * Note: The returned array must be malloced, assume caller calls free().
 */
int* circularGameLosers(int n, int k, int* returnSize){
    int* losers = (int*)malloc(n * sizeof(int));
    int numLosers = 0;

    int* received = (int*)calloc(n, sizeof(int)); // Array to track if a friend has received the ball
    int current = 0; // Start with the 1st friend
    int passCount = 0; // To count the number of passes made

    while (numLosers < n - 1) {
        passCount++;
        current = (current + k - 1) % n; // Calculate the next friend to receive the ball

        if (received[current] == 0) {
            received[current] = 1; // Mark the friend as having received the ball
            losers[numLosers++] = current + 1; // Friends are 1-indexed
        }
    }

    *returnSize = numLosers;
    free(received); // Free the temporary array
    return losers;
}
