class Solution {
  List<int> circularGameLosers(int n, int k) {
    List<bool> hasReceivedBall = List.filled(n, false); // Track if a friend has received the ball
    int currentFriend = 0; // Start with the 1st friend (indexed at 0)
    int turn = 1; // Turn count starts at 1

    while (true) {
      currentFriend = (currentFriend + (turn * k - 1)) % n; // Find the next friend to receive the ball

      // Check if this friend has already received the ball
      if (hasReceivedBall[currentFriend]) {
        break;
      }

      // Mark this friend as having received the ball
      hasReceivedBall[currentFriend] = true;
      turn++; // Increment turn count
    }

    // Gather the list of friends who never received the ball
    List<int> losers = [];
    for (int i = 0; i < n; i++) {
      if (!hasReceivedBall[i]) {
        losers.add(i + 1); // Add 1 because friends are numbered starting from 1
      }
    }

    return losers;
  }
}
