class Solution {
    public int[] circularGameLosers(int n, int k) {
        boolean[] hasBall = new boolean[n];
        int currentFriend = 0; // Start at the 1st friend, represented as index 0
        int step = 0;

        while (true) {
            // Calculate the friend to pass the ball to
            step++;
            currentFriend = (currentFriend + k) % n;

            // Check if this friend has received the ball before
            if (hasBall[currentFriend]) {
                break; // End the game if a friend receives the ball for the second time
            }
            hasBall[currentFriend] = true;
        }

        // Count and identify the friends who never received the ball
        int countLosers = 0;
        for (boolean receivedBall : hasBall) {
            if (!receivedBall) {
                countLosers++;
            }
        }

        // Store the losers in an array
        int[] losers = new int[countLosers];
        int index = 0;
        for (int i = 0; i < n; i++) {
            if (!hasBall[i]) {
                losers[index++] = i + 1; // Convert 0-based index to 1-based friend number
            }
        }

        return losers;
    }
}
