class Solution {
    public int[] circularGameLosers(int n, int k) {
        boolean[] receivedBall = new boolean[n];
        int currentFriend = 0; // Starting at friend 1, indexed as 0
        int turn = 0;

        while (true) {
            turn++;
            currentFriend = (currentFriend + (turn * k - 1)) % n;

            if (receivedBall[currentFriend]) {
                break; // The game ends when a friend receives the ball for the second time
            }

            receivedBall[currentFriend] = true;
        }

        // Counting friends who never received the ball
        int countLosers = 0;
        for (boolean hasReceived : receivedBall) {
            if (!hasReceived) {
                countLosers++;
            }
        }

        // Collecting the losers in an array
        int[] losers = new int[countLosers];
        int index = 0;
        for (int i = 0; i < n; i++) {
            if (!receivedBall[i]) {
                losers[index++] = i + 1; // Adjusting for 1-based indexing
            }
        }

        return losers;
    }
}
