class Solution {

    /**
     * @param Integer $n
     * @param Integer $k
     * @return Integer[]
     */
    function circularGameLosers($n, $k) {
        $receivedBall = array_fill(0, $n, false);
        $current = 0;
        $losers = [];

        for ($i = 1; $i <= $n; $i++) {
            $current = ($current + $k - 1) % $n; // Adjusted calculation for next friend
            if ($receivedBall[$current]) {
                break;
            }
            $receivedBall[$current] = true;

            // Move to the next turn without incrementing $i if the same friend receives the ball again
            if ($current === ($current + $k - 1) % $n) {
                continue;
            }
        }

        for ($j = 0; $j < $n; $j++) {
            if (!$receivedBall[$j]) {
                $losers[] = $j + 1; // Adjust index to 1-based
            }
        }

        return $losers;
    }
}
