class Solution {
    func circularGameLosers(_ n: Int, _ k: Int) -> [Int] {
        var hasBall = Array(repeating: false, count: n)
        var current = 0 // Starting from the first friend
        var steps = 1

        // Continue until a friend receives the ball for the second time
        while true {
            // Calculate next friend to receive the ball
            current = (current + steps * k - 1) % n
            if hasBall[current] {
                // Friend receives the ball for the second time, end loop
                break
            }
            // Mark the friend as having received the ball
            hasBall[current] = true
            steps += 1
        }

        // Collect losers who did not receive the ball
        var losers = [Int]()
        for i in 0..<n {
            if !hasBall[i] {
                losers.append(i + 1) // Adjusting index to match friend numbering
            }
        }

        return losers
    }
}
