class Solution {
    func circularGameLosers(_ n: Int, _ k: Int) -> [Int] {
        var players = Array(1...n) // Create an array of players from 1 to n
        var losers = [Int]() // Initialize an empty array to hold the losers
        var ballPosition = 0 // Start with the first player having the ball

        // Repeat until only one player is left without receiving the ball twice
        while players.count > 1 {
            // Calculate the next player to receive the ball
            // The '- 1' accounts for the zero-based indexing of arrays
            ballPosition = (ballPosition + k - 1) % players.count 
            // Remove the player who receives the ball from the list, as they cannot lose
            losers.append(players.remove(at: ballPosition))
        }

        // The remaining player(s) in 'players' have not received the ball twice
        // Therefore, they are the losers. But since the game ends when one player
        // receives the ball twice, everyone removed is actually a winner, and those
        // not removed are the losers. We need to invert our approach.
        let winners = Set(losers)
        losers = (1...n).filter { !winners.contains($0) }

        return losers.sorted() // Return the losers in ascending order
    }
}
