class Solution {
    func circularGameLosers(_ n: Int, _ k: Int) -> [Int] {
        var receivedBall = Set<Int>() // Track friends who have received the ball
        var currentPosition = 0 // Start with the 1st friend
        var passCount = 1 // The pass count starts at 1

        // Simulate the game until a friend receives the ball for the second time
        while receivedBall.count < n {
            // Calculate the friend to receive the ball in the current pass
            currentPosition = (currentPosition + (passCount * k) - 1) % n
            if receivedBall.contains(currentPosition + 1) {
                // If the current friend has already received the ball, end the game
                break
            }
            // Mark the current friend as having received the ball
            receivedBall.insert(currentPosition + 1)
            passCount += 1
        }

        // Identify the losers (those who never received the ball)
        let losers = (1...n).filter { !receivedBall.contains($0) }

        return losers
    }
}
