function circularGameLosers(n: number, k: number): number[] {
    // Array to track whether a friend has received the ball
    let receivedBall = new Array(n).fill(false);

    // Current position of the ball, starting from the 1st friend
    let currentPosition = 0; 

    // Variable to track the turn number
    let turn = 1;

    while (true) {
        // Calculate the next position
        currentPosition = (currentPosition + turn * k - 1) % n;

        if (receivedBall[currentPosition]) {
            // If the friend already received the ball, the game ends
            break;
        }

        // Mark the current friend as having received the ball
        receivedBall[currentPosition] = true;

        // Increment the turn number for the next pass
        turn++;
    }

    // Find all friends who never received the ball
    let losers = [];
    for (let i = 0; i < n; i++) {
        if (!receivedBall[i]) {
            losers.push(i + 1); // Adjust index to match friend number (1-based)
        }
    }

    return losers;
}
