class Solution {
public:
    int furthestDistanceFromOrigin(string moves) {
        int leftMoves = 0; // To count 'L' and '_'
        int rightMoves = 0; // To count 'R' and '_'

        // Counting 'L', 'R', and '_'
        for (char move : moves) {
            if (move == 'L' || move == '_') {
                leftMoves++;
            }
            if (move == 'R' || move == '_') {
                rightMoves++;
            }
        }

        // The furthest distance from origin is the maximum of left or right moves,
        // but since '_' can be either, we take the maximum of total 'L' and 'R' moves.
        // This is because the optimal strategy involves using all '_' as either 'L' or 'R'
        // depending on which direction gives the maximum distance.
        return std::max(leftMoves, rightMoves) - (moves.find('_') != string::npos ? 0 : 1); // Adjust if there's no '_'
    }
};
