func furthestDistanceFromOrigin(moves string) int {
    leftMoves, rightMoves, freeMoves := 0, 0, 0

    // Count the number of L, R, and _ moves.
    for _, move := range moves {
        switch move {
        case 'L':
            leftMoves++
        case 'R':
            rightMoves++
        case '_':
            freeMoves++
        }
    }

    // Calculate the net distance from the origin without considering free moves.
    netDistance := rightMoves - leftMoves

    // The furthest distance is the absolute value of net distance plus all the free moves.
    // This is because free moves can be used to either increase the distance to the right or left depending on the net distance.
    furthestDistance := abs(netDistance) + freeMoves

    return furthestDistance
}

// Helper function to calculate the absolute value.
func abs(x int) int {
    if x < 0 {
        return -x
    }
    return x
}
