class Solution {
    public int furthestDistanceFromOrigin(String moves) {
        int underscoreCount = 0; // To keep track of '_' characters
        int leftCount = 0; // To keep track of 'L' characters
        int rightCount = 0; // To keep track of 'R' characters

        // Counting 'L', 'R', and '_' characters in the string
        for (int i = 0; i < moves.length(); i++) {
            char move = moves.charAt(i);
            if (move == 'L') {
                leftCount++;
            } else if (move == 'R') {
                rightCount++;
            } else {
                underscoreCount++;
            }
        }

        // The maximum distance from origin can be achieved by moving all the '_' characters towards
        // the direction which already has more moves between 'L' and 'R'
        // If 'L' and 'R' are equal, we can choose either direction for '_'
        int maxDistance;
        if (leftCount > rightCount) {
            // Move all '_' towards 'L' to maximize the distance in the left direction
            maxDistance = leftCount + underscoreCount - rightCount;
        } else {
            // Move all '_' towards 'R' to maximize the distance in the right direction
            maxDistance = rightCount + underscoreCount - leftCount;
        }

        return maxDistance;
    }
}
