class Solution {
    fun furthestDistanceFromOrigin(moves: String): Int {
        var maxDistance = 0
        var currentPosition = 0
        var flexibleMoves = 0 // Tracks the number of '_' for flexibility in moves

        // Calculate the initial position without considering '_'
        for (move in moves) {
            when (move) {
                'L' -> currentPosition--
                'R' -> currentPosition++
                '_' -> flexibleMoves++ // Accumulate flexible moves
            }
            // Update max distance at each step
            maxDistance = maxOf(maxDistance, kotlin.math.abs(currentPosition))
        }

        // The furthest point is determined by the absolute value of the currentPosition
        // plus all the flexible moves since they can be used to either extend the current direction
        // or move back towards the origin then further away in the opposite direction.
        // However, the furthest distance from the origin after using flexible moves
        // depends only on the initial absolute distance plus the flexible moves,
        // as they can all be used to move in the most beneficial direction.
        return maxDistance + flexibleMoves
    }
}
