object Solution {
    def furthestDistanceFromOrigin(moves: String): Int = {
        // Calculate the difference in steps between 'L' and 'R' ignoring '_'
        val initialDiff = moves.count(_ == 'L') - moves.count(_ == 'R')
        // Count the number of '_' which can be used to adjust the distance
        val underscoreCount = moves.count(_ == '_')

        // The furthest distance can be achieved by moving all the way in one direction
        // and then using '_' to move further in the same direction or to balance the steps
        // if initialDiff is positive, it means more 'L' so we can use '_' as 'R' to go further left (negative)
        // if initialDiff is negative, it means more 'R' so we can use '_' as 'L' to go further right (positive)
        // if initialDiff is zero, all '_' contribute to the furthest distance directly
        Math.abs(initialDiff) + underscoreCount
    }
}
